checkitem.compliant:
(
       ( COMPONENT = 'EHSM' and VERSION = '600' and not( to_integer( COALESCE(NULLIF(SP,''),'0') ) <    1 )   ) 
     )

checkitem.noncompliant:
(
       ( COMPONENT = 'EHSM' and VERSION = '600' and to_integer( COALESCE(NULLIF(SP,''),'0') ) <    1 )  
     )

(
 ( COMPONENT = 'EHSM' and VERSION = '600' and to_integer( COALESCE(NULLIF(SP,''),'0' ) ) between    0 and   0 )  
  )
