checkitem.compliant:
(
       ( COMPONENT = 'GBX01HR' and VERSION = '600' and not( to_integer( COALESCE(NULLIF(SP,''),'0') ) <   11 )   ) 
     )

checkitem.noncompliant:
(
       ( COMPONENT = 'GBX01HR' and VERSION = '600' and to_integer( COALESCE(NULLIF(SP,''),'0') ) <   11 )  
     )

(
 ( COMPONENT = 'GBX01HR' and VERSION = '600' and to_integer( COALESCE(NULLIF(SP,''),'0' ) ) between    2 and   10 )  
  )

(
 ( COMPONENT = 'ST' and VERSION = '720' and to_integer( COALESCE(NULLIF(SP,''),'0' ) ) between    7 and    7 )  
  )
