checkitem.compliant:
(
       ( COMPONENT = 'GBX01HR' and VERSION = '600' and not( to_integer( COALESCE(NULLIF(SP,''),'0') ) <   12 )   ) 
     )

checkitem.noncompliant:
(
       ( COMPONENT = 'GBX01HR' and VERSION = '600' and to_integer( COALESCE(NULLIF(SP,''),'0') ) <   12 )  
     )

(
      ( COMPONENT = 'GBX01HR' and VERSION = '600' and to_integer( COALESCE(NULLIF(SP,''),'0' ) ) between    4 and   11 )  
  )

