checkitem.compliant:
(
       ( COMPONENT = 'S4FPSL' and VERSION = '100' and not( to_integer( REPLACE_REGEXPR( '\.' IN (COALESCE(NULLIF(SP,''),'0')) with '')) <     3 )   ) 
     )

checkitem.noncompliant:
(
       ( COMPONENT = 'S4FPSL' and VERSION = '100' and to_integer( REPLACE_REGEXPR( '\.' IN (COALESCE(NULLIF(SP,''),'0')) with '')) <     3 )  
     )

(
   ( COMPONENT = 'S4FPSL' and VERSION = '100' and to_integer( REPLACE_REGEXPR('\.' IN (COALESCE(NULLIF(SP,''),'0')) with '')) between     0 and    2 )  
  )
