checkitem.compliant:
( ( COMPONENT = 'S4CORE' and VERSION = '107' and not( (lpad(SP,4,'0')) < '0006' ) )
or ( COMPONENT = 'S4CORE' and VERSION = '108' and not( (lpad(SP,4,'0')) < '0004' ) )
)

checkitem.noncompliant:
( ( COMPONENT = 'S4CORE' and VERSION = '107' and (lpad(SP,4,'0')) < '0006' )
or ( COMPONENT = 'S4CORE' and VERSION = '108' and (lpad(SP,4,'0')) < '0004' )
)

( ( COMPONENT = 'S4CORE' and VERSION = '107' and lpad(SP,4,'0') between '0000' and '0005' )
or ( COMPONENT = 'S4CORE' and VERSION = '108' and lpad(SP,4,'0') between '0000' and '0003' )
)

