checkitem.noncompliant:
(
      ( COMPONENT = 'S4CORE' and VERSION = '108' and (lpad(SP,4,'0'))  < '0004' )  

     )

checkitem.compliant:
( ( COMPONENT = 'S4CORE' and VERSION = '107' and not( (lpad(SP,4,'0')) < '0005' ) )
or ( COMPONENT = 'S4CORE' and VERSION = '108' and not( (lpad(SP,4,'0')) < '0003' ) )
)

checkitem.noncompliant:
( ( COMPONENT = 'S4CORE' and VERSION = '107' and (lpad(SP,4,'0')) < '0005' )
or ( COMPONENT = 'S4CORE' and VERSION = '108' and (lpad(SP,4,'0')) < '0003' )
)

( ( COMPONENT = 'S4CORE' and VERSION = '107' and lpad(SP,4,'0') between '0000' and '0004' )
or ( COMPONENT = 'S4CORE' and VERSION = '108' and lpad(SP,4,'0') between '0000' and '0002' )
)
